// if true, ingredients will be added to Actor immediately, instead of being created in the World
const addToActor = false; 

// if true and `addToActor` is enabled, then money will be automatically deducted from Actor to pay for the ingredient
const autoPay = true;

// how many items to add/buy with `addToActor` enabled
const quantity = 1;


const macros = game.modules.get("forien-armoury")?.api.macros;
await macros.createIngredient(this, {addToActor, autoPay, quantity});
